mapper.select:
SELECT user_id, user_nm, passwd, email, phone_no, nationality, language, join_date FROM TB_USER
user_id = #{userId}
and email = #{email}
LIMIT 1

mapper.update:
UPDATE TB_USER 
   SET 

   	   user_nm     = #{userNm}
     , email       = #{email}
     , phone_no    = #{phoneNo}
     , nationality = LOWER(#{nationality})
     , language    = LOWER(#{language})


       passwd	   = #{passwd}




		user_id = #{userId}


		email = #{email}
